import jxl.*
import java.io.*
import com.eviware.soapui.model.testsuite.TestRunner.Status
def sku,name,price, attributeSetId, typeId
def filepath = new File("C:\\Users\\AnithaRaman\\Desktop\\SDET\\API\\SOAP_Project\\SampleData1.xls")
def workbook = Workbook.getWorkbook(filepath)
def sh = workbook.getSheet(0)

def rowsize = sh.getRows().toInteger();
def columnsize = sh.getColumns().toInteger();

log.info "$rowsize and $columnsize"

for(def i=1; i<rowsize; i++)
{

					sku=sh.getCell(0,i).getContents()
					name=sh.getCell(1,i).getContents()
					price=sh.getCell(2,i).getContents()
					attributeSetId=sh.getCell(3,i).getContents()
					typeId=sh.getCell(4,i).getContents()
					log.info "$sku"
					log.info "$name"


// get TestCase
def tc = testRunner.testCase.testSuite.project.testSuites["Functional Testing"].testCases["Property Expansion CreateViewDeleteProduct"]
// set credential properties from the script itself
tc.testSteps["product Properties"].setPropertyValue("sku", sku )
tc.testSteps["product Properties"].setPropertyValue("name", name )
tc.testSteps["product Properties"].setPropertyValue("price", price )
tc.testSteps["product Properties"].setPropertyValue("attributeSetId", attributeSetId)
tc.testSteps["product Properties"].setPropertyValue("typeId", typeId )
// run test synchrouously
def runner = tc.run( null, false )

// show the test case result status
log.info "Status: $runner.status, time taken for TestCase was: $runner.timeTaken ms"
log.info "Number of test steps which ran: " + runner.results.size()

// assert that it didn't fail
assert runner.status != Status.FAILED : runner.reason
// assert that only expected number of steps ran!!
//assert runner.results.size() == 5

}

